import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import axios from 'axios';
import moment from 'moment';
import BtnTareaProgress from './BtnTareaProgress';
import BtnTareaCompleta from './BtnTareaCompleta';
import BtnTareaEliminada from './BtnTareaEliminada';

const Task = () => {
  // Variables de estado
  const [accion, setAccion] = useState('');
  const [status, setStatus] = useState('uno');
  const [fechainicio, setinicio] = useState('');
  const [fechavencimiento, setFechavencimiento] = useState('');
  const [claseTarea, settipotarea] = useState('');  
  const [hora, sethora] = useState('');
  const [descripcion, setDescripcion] = useState(''); // Nueva variable de estado para el textarea
  const [projects, setProjects] = useState([]);

  const fetchTareas = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/tareas');
      setProjects(response.data);
    } catch (error) {
      console.error('Error al obtener las tareas:', error);
    }
  };

  const addProyecto = (e) => {
    e.preventDefault();
    const nuevaTarea = {
      accion,           // Usar el valor del estado
      fechainicio,      // Usar el valor del estado
      fechavencimiento, // Usar el valor del estado
      status,           // Usar el valor del estado
      claseTarea,       // Usar el valor del estado
      hora ,        // Usar el valor del estado
      descripcion       // Usar el valor del estado
    };
    axios.post(`http://localhost:8000/api/creartarea`, nuevaTarea)
      .then((res) => {
        console.log(res);
        fetchTareas(); // Llamar a fetchTareas para actualizar la lista de tareas despuÃ©s de crear una nueva tarea
      }).catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    fetchTareas();
  }, []);
 
 

  return (
    <>
    <Navbar />
    <div className="container mx-auto my-10 p-5">
      <div className="flex flex-wrap -mx-2">
        <div className="w-full md:w-2/3 px-2 mb-4 md:mb-0">
          <div className="flex flex-col items-center justify-center mt-10 mx-10">
            <h2 className="text-2xl font-semibold mb-4">Tareas</h2>
            <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-4xl">
              {/* Tablero de Tareas */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {projects.map((pro) => (
                  <div key={pro._id} className="task-card-border text-gray-700 border-blue-500 border-4 p-2 m-2 rounded-[20px]">
                    <h4><strong>Recordar:</strong> {pro.accion}</h4>
                    <p><strong>Inicio:</strong> {moment(pro.fechainicio).format('MM-DD-YYYY')}</p>
                    <p><strong>Vencimiento :</strong> {moment(pro.fechavencimiento).format('MM-DD-YYYY')}</p>
                    <p><strong>Hora de NotificaciÃ³n:</strong> {pro.hora}</p>
                    <p><strong>Tipo:</strong> {pro.claseTarea}</p>
                    <p><strong>Mas InformaciÃ³n:</strong> {pro.descripcion}</p>
                    {/* Botones de acciones */}
                    <button className="btn-progress bg-green-400 border-green-900 border-2 rounded-[20px] px-2 py-1" onClick={() => updateStatus(pro._id, 'dos')}>Iniciar</button>
                    <button className="btn-complete bg-yellow-400 border-yellow-900 border-2 rounded-[20px] px-2 py-1" onClick={() => updateStatus(pro._id, 'tres')}>Finalizar</button>
                    <button className="btn-remove bg-red-400 border-red-900 border-2 rounded-[20px] px-2 py-1" onClick={() => removeProject(pro._id)}>Eliminar</button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
        <div className="w-full md:w-1/3 px-2">
          <div className="flex flex-col items-center justify-center mt-10 mx-10">
            <h2 className="text-2xl font-semibold mb-4">Actualice su Tablero</h2>
            <form className="bg-white shadow-md rounded-lg p-6 w-full max-w-md" onSubmit={addProyecto}>
              <div className="mb-4">
                <label className="block text-gray-700">Descripcion</label>
                <input type="text" className="w-full px-4 py-2 mt-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-blue-600" value={accion} onChange={(e) => setAccion(e.target.value)} />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">Estado</label>
                <select className="w-full px-4 py-2 mt-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-blue-600" value={status} onChange={(e) => setStatus(e.target.value)}>
                  <option value="uno">Pendiente</option>
                  <option value="dos">En Curso</option>
                  <option value="tres">Finalizada</option>
                </select>
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">Fecha Inicio</label>
                <input type="date" className="w-full px-4 py-2 mt-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-blue-600" value={fechainicio} onChange={(e) => setFechainicio(e.target.value)} />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">Fecha Fin</label>
                <input type="date" className="w-full px-4 py-2 mt-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-blue-600" value={fechavencimiento} onChange={(e) => setFechavencimiento(e.target.value)} />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">Tipo</label>
                <select className="w-full px-4 py-2 mt-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-blue-600" value={claseTarea} onChange={(e) => setClaseTarea(e.target.value)}>
                  <option value="Personal">Personal</option>
                  <option value="Hogar">Hogar</option>
                  <option value="Trabajo">Trabajo</option>
                  <option value="Academico">Academico</option>
                  <option value="Otro">Otro</option>
                </select>
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">Hora de NotificaciÃ³n</label>
                <input type="time" className="w-full px-4 py-2 mt-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-blue-600" value={hora} onChange={(e) => setHora(e.target.value)} />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">DescripciÃ³n Adicional</label>
