  <>
  <Navbar/>
    <div className="flex h-[100vh] bg-white">
      <div className="w-2/3 mb-2">
        <div className="flex flex-col justify-center items-stretch mt-10 m-10">
          <div className='overflow-auto bg-white shadow-2xl flex w-full flex-col items-center rounded-20px w-400px mx-auto m-2 p-4 bg-white bg-clip-border shadow-3xl shadow-shadow-500 dark:!bg-navy-800 dark:text-white dark:!shadow-none'>
            <div className='dashboard-tab'>
              <table className='table-fixed w-full text-center'>
                <thead className='border-b-4 border-blue-900'>
                  <tr>
                    <th className='col-1 text-2xl text-blue-800'>Pendiente</th>
                    <th className='col-2 text-2xl text-blue-800'>En Curso</th>
                    <th className='col-3 text-2xl text-blue-800'>Finalizada</th>
                  </tr>
                </thead>
                <tbody className='align-top'>
                  <tr>
                    <td className='td-css'>
                      <div className='td-css grid grid-cols-1 gap-4 p-2 m-2'>
                        {projects.filter(pro => pro.status === 'Pendiente').map(pro => (
                          <div key={pro._id} className='task-card-border border-blue-500 border-4 p-2 m-2 rounded-20px'>
                            <h4>{pro.accion}</h4>
                            <h6>Fecha Inicio: {moment(pro.fechainicio).format('MM-DD-YYYY')}</h6>
                            <BtnTareaProgress projectId={pro._id} Update={displayProjects} />
                            <h6>Fecha Vencimiento: {moment(pro.fechavencimiento).format('MM-DD-YYYY')}</h6>
                          </div>
                        ))}
                      </div>
                    </td>
                    <td className='td-css'>
                      <div className='grid grid-cols-1 gap-4 p-2 m-2'>
                        {projects.filter(pro => pro.status === 'En Curso').map(pro => (
                          <div key={pro._id} className='task-card-border text-gray-700 border-blue-500 border-4 p-2 m-2 rounded-20px'>
                            <h4>{pro.accion}</h4>
                            <h6>Fecha: {moment(pro.fechavencimiento).format('MM-DD-YYYY')}</h6>
                            <BtnTareaCompleta projectId={pro._id} Update={displayProjects} />
                          </div>
                        ))}
                      </div>
                    </td>
                    <td className='td-css'>
                      <div className='grid grid-cols-1 gap-4 p-2 m-2'>
                        {projects.filter(pro => pro.status === 'Finalizado').map(pro => (
                          <div key={pro._id} className='task-card-border text-gray-700 border-blue-500 border-4 p-2 m-2 rounded-20px'>
                            <h4>{pro.accion}</h4>
                            <h6>Fecha: {moment(pro.fechavencimiento).format('MM-DD-YYYY')}</h6>
                            <BtnTareaEliminada projectId={pro._id} Update={displayProjects} />
                          </div>
                        ))}
                      </div>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
      <div className="w-1/3 mb-2">
        <div className="flex flex-col justify-center items-center mt-20 m-20">
          <form onSubmit={addProyecto} className="border-0 border-blue-900 shadow-2xl flex w-full flex-col items-center rounded-20px w-400px mx-auto p-4 bg-white bg-clip-border shadow-3xl shadow-shadow-500 dark:!bg-navy-800 dark:text-white dark:!shadow-none">
            <div className="mt-7 mb-5 flex w-full flex-col items-center">
              <h3 className="text-2xl sm:text-2xl md:text-3xl xl:text-3xl 2xl:text-3xl font-bold text-blue-900 dark:text-white">Actividades</h3>
              <p className="text-2xl sm:text-2xl md:text-3xl xl:text-3xl 2xl:text-4xl font-normal text-orange-500"></p>
            </div>
            <div className="mt-6 mb-3 flex flex-wrap gap-10 md:gap-5">
              <div className="flex flex-col items-center justify-center">
                <p className="text-1xl sm:text-1xl md:text-1xl xl:text-2xl 2xl:text-2xl font-normal text-blue-900">Actualice su Tablero</p>
              </div>
            </div>
            <div className='flex flex-col w-full'>
              <div>
                <label className="text-1xl text-blue-900" htmlFor="accion">Descripcion</label>
                <input id="accion" type="text" className="block w-full px-4 py-2 mt-2 bg-white border border-blue-900 rounded-md dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring" placeholder='Descripcion' onChange={(e) => setAccion(e.target.value)} value={accion} />
              </div>
              <div>
                <label className="text-1xl text-blue-900" htmlFor="status">Estado</label>
                <select id="status" className="block w-full px-4 py-2 mt-2 bg-white border border-blue-900 rounded-md dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring" onChange={(e) => setStatus(e.target.value)} value={status}>
                  <option value="Pendiente">Pendiente</option>
                  <option value="En Curso">En Curso</option>
                  <option value="Finalizado">Finalizado</option>
                </select>
              </div>
              <div>
                <label className="text-1xl text-blue-900" htmlFor="fechainicio">Fecha Inicio</label>
                <input id="fechainicio" type="date" className="block w-full px-4 py-2 mt-2 bg-white border border-blue-900 rounded-md dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring" onChange={(e) => setinicio(e.target.value)} value={fechainicio} />
              </div>
              <div>
                <label className="text-1xl text-blue-900" htmlFor="fechavencimiento">Fecha Fin</label>
                <input id="fechavencimiento" type="date" className="block w-full px-4 py-2 mt-2 bg-white border border-blue-900 rounded-md dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring" onChange={(e) => setFechavencimiento(e.target.value)} value={fechavencimiento} />
              </div>
              <div>
                <label className="text-1xl text-blue-900" htmlFor="claseTarea">Tipo</label>
                <select id="claseTarea" className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-blue-900 rounded-md dark:text-gray-300 dark:border-gray-600 focus:border-blue-500" onChange={(e) => settipotarea(e.target.value)} value={claseTarea}>
                  <option value="Hogar">Hogar</option>
                  <option value="Administrativo">Administrativo</option>
                  <option value="Academico">Académico</option>
                  <option value="Laboral">Laboral</option>
                  <option value="Personal">Personal</option>
                </select>
              </div>
              <div>
                <label className="text-1xl text-blue-900" htmlFor="hora">Hora de Notificación</label>
                <input id="hora" type="time" className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border border-blue-900 rounded-md dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring" onChange={(e) => sethora(e.target.value)} value={hora} />
              </div>
            </div>
            <div className='grid grid-cols-1 gap-6 mt-8'>
              <div className="flex justify-end mt-6">
                <button className="px-8 py-3 leading-5 text-white text-2xl transition-colors duration-200 transform bg-green-500 rounded-md hover:bg-green-700 focus:outline-none focus:bg-gray-600">Agregar Tarea</button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  </>
);
}

