import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Cuenta = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    
    useEffect(() => {
        axios.get('http://localhost:8000/api/usuarios')
            .then((res) => {
                setUser(res.data);
                setLoading(false);
            })
            .catch((err) => {
                console.log(err);
                setLoading(false);
            });
    }, []);
    
    if (loading) {
        return <div>Cargando datos...</div>;
    }

    if (!user) {
        return <div>No se encontraron datos del usuario</div>;
    }

    return (
        <div className="flex flex-col items-center justify-center mt-20 mx-20">
            <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md">
                <h2 className="text-2xl font-bold mb-4">Cuenta</h2>
                <div className="flex items-center justify-center mb-6">
                    <img
                        src={`https://ui-avatars.com/api/?name=${user.nombre}+${user.apellido}&background=random`}
                        alt="Avatar"
                        className="w-24 h-24 rounded-full"
                    />
                </div>
                <div className="mb-4">
                    <h3 className="text-xl font-bold">Datos Personales</h3>
                    <p><strong>Nombre:</strong> {user.nombre}</p>
                    <p><strong>Apellido:</strong> {user.apellido}</p>
                    <p><strong>Nro. Teléfono:</strong> {user.telefono}</p>
                    <p><strong>Correo Electrónico:</strong> {user.email}</p>
                </div>
                <div className="mb-4">
                    <h3 className="text-xl font-bold">Datos Adicionales</h3>
                    <p><strong>Cargo:</strong> {user.cargo}</p>
                </div>
            </div>
        </div>
    );
}

export default Cuenta;
