import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import axios from 'axios';
import moment from 'moment';
import BtnTareaProgress from './BtnTareaProgress';
import BtnTareaCompleta from './BtnTareaCompleta';
import BtnTareaEliminada from './BtnTareaEliminada';
import './Task.css';

const Task = () => {
  const [accion, setAccion] = useState('');
  const [status, setStatus] = useState('Pendiente');
  const [fechainicio, setinicio] = useState('');
  const [fechavencimiento, setFechavencimiento] = useState('');
  const [claseTarea, settipotarea] = useState('');
  const [hora, sethora] = useState('');

  const addProyecto = (e) => {
    e.preventDefault();
    axios.post(`http://localhost:8000/api/creartarea`, {
      accion, fechainicio, fechavencimiento, status, claseTarea, hora
    }).then((res) => {
      console.log(res.data);
    }).catch((err) => {
      console.log(err.response.data); // Mostrar el mensaje de error completo
    });
  };

  const [projects, setProjects] = useState([]);

  const displayProjects = () => {
    axios.get('http://localhost:8000/api/tareas')
      .then((res) => {
        setProjects(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    displayProjects();
  }, []);

  return (
    <>
      <Navbar />
      <div className="flex h-[100vh] bg-white">
        <div className="w-2/3 mb-2">
          <div className="task-board">
            <div className="task-column">
              <h3>Pendiente</h3>
              {projects.filter(pro => pro.status === 'Pendiente').map(pro => (
                <div key={pro._id} className='task-card'>
                  <h4>{pro.accion}</h4>
                  <p>Fecha Inicio: {moment(pro.fechainicio).format('DD-MM-YYYY')}</p>
                  <p>Fecha Vencimiento: {moment(pro.fechavencimiento).format('DD-MM-YYYY')}</p>
                  <BtnTareaProgress projectId={pro._id} Update={displayProjects} />
                </div>
              ))}
            </div>
            <div className="task-column">
              <h3>En Curso</h3>
              {projects.filter(pro => pro.status === 'En Curso').map(pro => (
                <div key={pro._id} className='task-card'>
                  <h4>{pro.accion}</h4>
                  <p>Fecha Inicio: {moment(pro.fechainicio).format('DD-MM-YYYY')}</p>
                  <p>Fecha Vencimiento: {moment(pro.fechavencimiento).format('DD-MM-YYYY')}</p>
                  <BtnTareaCompleta projectId={pro._id} Update={displayProjects} />
                </div>
              ))}
            </div>
            <div className="task-column">
              <h3>Finalizada</h3>
              {projects.filter(pro => pro.status === 'Finalizado').map(pro => (
                <div key={pro._id} className='task-card'>
                  <h4>{pro.accion}</h4>
                  <p>Fecha Inicio: {moment(pro.fechainicio).format('DD-MM-YYYY')}</p>
                  <p>Fecha Vencimiento: {moment(pro.fechavencimiento).format('DD-MM-YYYY')}</p>
                  <BtnTareaEliminada projectId={pro._id} Update={displayProjects} />
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="w-1/3 mb-2">
          <div className="task-form">
            <form onSubmit={addProyecto}>
              <h3>Actualice su Tablero</h3>
              <input type="text" placeholder="Descripcion" onChange={(e) => setAccion(e.target.value)} value={accion} />
              <select onChange={(e) => setStatus(e.target.value)} value={status}>
                <option value="Pendiente">Pendiente</option>
                <option value="En Curso">En Curso</option>
                <option value="Finalizado">Finalizado</option>
              </select>
              <input type="date" onChange={(e) => setinicio(e.target.value)} value={fechainicio} />
              <input type="date" onChange={(e) => setFechavencimiento(e.target.value)} value={fechavencimiento} />
              <select onChange={(e) => settipotarea(e.target.value)} value={claseTarea}>
                <option value="Hogar">Hogar</option>
                <option value="Administrativo">Administrativo</option>
                <option value="Academico">Acad√©mico</option>
                <option value="Laboral">Laboral</option>
                <option value="Personal">Personal</option>
              </select>
              <input type="time" onChange={(e) => sethora(e.target.value)} value={hora} />
              <button type="submit">Agregar Tarea</button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default Task;
