import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Cuenta.css'; // Tu archivo de estilos

const Cuenta = () => {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [email, setEmail] = useState('');
  const [telefono, setTelefono] = useState('');

  useEffect(() => {
    // Cargar datos del usuario
    axios.get('http://localhost:8000/api/user/profile', { withCredentials: true })
      .then(res => {
        setNombre(res.data.nombre);
        setApellido(res.data.apellido);
        setEmail(res.data.email);
        setTelefono(res.data.telefono);
      })
      .catch(err => {
        console.log(err);
      });
  }, []);

  const formSubmitHandler = (e) => {
    e.preventDefault();
    const updatedUser = { nombre, apellido, email, telefono };

    axios.put('http://localhost:8000/api/user/update', updatedUser, { withCredentials: true })
      .then(res => {
        console.log(res);
        // Manejar la respuesta si es necesario
      })
      .catch(err => {
        console.log(err);
      });
  };

  return (
    <div className="cuenta-container mx-auto p-10">
      <h1 className="text-3xl font-bold text-center mb-8">Cuenta</h1>
      <div className="flex justify-between items-start">
        <div className="profile-image-container w-1/4">
          <img src="path/to/profile-image.jpg" alt="Perfil" className="rounded-full mx-auto" />
          <button className="btn-edit-image bg-blue-500 text-white py-2 px-4 rounded mt-4 block mx-auto">Editar Imagen</button>
        </div>
        <div className="profile-details-container w-3/4 bg-gray-100 p-8 rounded shadow-md">
          <h2 className="text-2xl font-semibold mb-4">Datos Personales</h2>
          <form onSubmit={formSubmitHandler}>
            <div className="mb-4">
              <label className="block text-gray-700">Nombre</label>
              <input
                type="text"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)}
                className="block w-full px-4 py-2 mt-2 bg-white border border-gray-300 rounded-md focus:border-blue-500 focus:outline-none"
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700">Apellido</label>
              <input
                type="text"
                value={apellido}
                onChange={(e) => setApellido(e.target.value)}
                className="block w-full px-4 py-2 mt-2 bg-white border border-gray-300 rounded-md focus:border-blue-500 focus:outline-none"
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700">Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="block w-full px-4 py-2 mt-2 bg-white border border-gray-300 rounded-md focus:border-blue-500 focus:outline-none"
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700">Tel√©fono</label>
              <input
                type="text"
                value={telefono}
                onChange={(e) => setTelefono(e.target.value)}
                className="block w-full px-4 py-2 mt-2 bg-white border border-gray-300 rounded-md focus:border-blue-500 focus:outline-none"
              />
            </div>
            <button type="submit" className="bg-orange-500 text-white py-2 px-4 rounded mt-4">Guardar</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Cuenta;
